<?php

$textAreaHtmlId = YtilsRexMd::createId();
$csrfToken = YtilsRexMd::createAndRegisterCsrfToken();

/** @var $this rex_article_content_editor */
$currentArticleId = $this->getArticleId();
$currentArticle = rex_article::get($currentArticleId);

$addon = rex_addon::get("ytils_rex_md");
$yupputItemCount = $addon->getConfig("ytils_rex_md_config_number_of_yupput_items");
if (!$yupputItemCount) {
    $yupputItemCount = 4;
}

$placeholderText = $addon->i18n("ytils_rex_md_yupput_config_placeholder");
$launchText = $addon->i18n("ytils_rex_md_opener_text");
$markdownText = $addon->i18n("ytils_rex_md_markdown_documentation");
$articleTitle = htmlentities($currentArticle->getName());

?><div class="ytilsRexMd">
    <div class="ytilsRexMdEditor">
        <div class="r">
            <span data-attach="<?php echo $textAreaHtmlId; ?>" class="ytilsRexMdExpandButton"><?php echo $launchText; ?></span>
            <img data-attach="<?php echo $textAreaHtmlId; ?>" class="ytilsRexMdExpandButton" src="<?php echo YtilsRexMd::EXPAND_IMAGE_DATA; ?>" alt="" />
        </div>
        <div class="ytilsRexMdTextArea">
            <label>
                <script>
                    (function() {

                        "use strict";
                        if (!window.Ytils) {

                            window.Ytils = { };
                        }

                        window.Ytils.YtilsRexMd = { };
                        window.Ytils.YtilsRexMd.Config = <?php echo YtilsRexMd::getRexMdConfig(); ?>;
                        window.Ytils.YtilsRexMd.YupputMpData = <?php echo YtilsRexMd::getYupputMediaItemsJson(); ?>;
                        window.Ytils.YtilsRexMd.YupputArtData = <?php echo YtilsRexMd::getYupputArticleItemsJson(); ?>;
                        window.Ytils.YtilsRexMd.isBackend = <?php echo rex::isBackend() ? 'true' : 'false'; ?>;

                    }());
                </script>
                <textarea data-csrf="<?php echo $csrfToken; ?>" data-placeholder="<?php echo $placeholderText; ?>" data-articleurl="<?php echo rex_getUrl($currentArticleId, rex_clang::getCurrentId()); ?>" data-articleid="<?php echo $currentArticleId; ?>" data-articletitle="<?php echo $articleTitle; ?>" data-i18npreviewerror="<?php echo $addon->i18n('ytils_rex_md_previewerror'); ?>"  data-i18nplaceholderarticles="<?php echo $addon->i18n('ytils_rex_md_yupput_config_placeholder_articles'); ?>" data-titlemediapoolbtn="<?php echo $addon->i18n('ytils_rex_md_tooltip_mediapoolbtn'); ?>" data-titlearticlesbtn="<?php echo $addon->i18n('ytils_rex_md_tooltip_articlesbtn'); ?>" data-titlepreviewbtn="<?php echo $addon->i18n('ytils_rex_md_tooltip_previewbtn'); ?>" data-titleclosebtn="<?php echo $addon->i18n('ytils_rex_md_tooltip_closebtn'); ?>" data-i18nplaceholdermediapool="<?php echo $addon->i18n('ytils_rex_md_yupput_config_placeholder_mediapool'); ?>" data-ajaxtargetmd="<?php echo YtilsRexMd::AJAX_URL_MD; ?>" id="<?php echo $textAreaHtmlId; ?>" class="ytilsRexMdTextArea" name="REX_INPUT_VALUE[1]">REX_VALUE[1]</textarea>
            </label>
        </div>
        <div class="r"><a href="https://github.com/adam-p/markdown-here/wiki/Markdown-Cheatsheet" target="_blank"><?php echo $markdownText; ?> <img src="data:image/png;base64," alt="Markdown" style="width: 20px; height: 12px;" /></a></div>
    </div>
</div>